import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Player } from '../../types/player';
import { FavoritesService } from 'src/app/favorites/favorites.service';

@Component({
  selector: 'app-player',
  templateUrl: './player.component.html',
  styleUrls: ['./player.component.css']
})


export class PlayerComponent {
  @Input() player: Player =  {} as Player; 

  addToFav() {

    this.favoritesService.add(this.player);
  }

  constructor(private favoritesService: FavoritesService) {}
}
<li class="player">
    <img [src]="player.image" [alt]="player.name" width="200" />
    <p class="title">{{ player.name }}</p>
    <p class="details">{{ player.club }} # {{ player.number}}</p>
    <p class="value">Value: {{player.value | currency: 'EUR'}}</p>
    <button class="favorites" *ngIf="!isFavorite" (click)="addToFav()">Add to favorite</button>
    <button *ngIf="isFavorite" (click)="addToFav()">Remove from favorites</button>
</li>
