import { Component, OnInit } from '@angular/core';
import { initializeApp } from 'firebase/app';
import { firebaseConfig } from './firebase.config';
import { AuthService } from './auth/auth.service';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent implements OnInit {

 ngOnInit(): void {
   initializeApp(firebaseConfig);
 }

 constructor(private authSerivce: AuthService) {}

 isAuthenticated() {
  return this.authSerivce.isAuthenticated;
 }
}

<nav class="flex justify-between p-4 bg-green-900 text-white" >
  <div>
    <a routerLink="">The Dream Team</a>
  </div>
  <div>
    <a class="mx-2" routerLink="favorites">My favorites</a>
    <ng-container *ngIf="!isAuthenticated()">
      <a class="mx-2" routerLink="login">Login</a>
      <a class="mx-2" routerLink="register">Register</a>
    </ng-container>
    <ng-container *ngIf="isAuthenticated()">
      <button (click)="logout()" class="mx-2">Log Out</button>
    </ng-container>

  </div>
</nav>
<div class="p-2">
    <router-outlet></router-outlet>
</div>
