import { Component } from '@angular/core';


interface Player {
  name: string
  club: string
  number: number
  position: string
  image: string
}


@Component({
  selector: 'app-books',
  templateUrl: './books.component.html',
  styleUrls: ['./books.component.css']
})


export class BooksComponent {
  players: Player[] = [
    {
      name: 'Lionel Messi',
      club: 'PSG',
      number: 30,
      position: 'Forward',
      image: 'https://th-i.thgim.com/public/incoming/nd5gr4/article66919750.ece/alternates/LANDSCAPE_660/2023-05-27T183821Z_1353986735_UP1EJ5R1FRVWK_RTRMADP_3_SOCCER-FRANCE-STR-PSG-REPORT.JPG'
    },
    {
      name: 'Neymar',
      club: 'PSG',
      number: 10,
      position: 'Forward',
      image: 'https://assets-webp.khelnow.com/news/uploads/2022/07/16420121570853-768x432.jpg.webp?imwidth=3840'
    },
    {
      name: 'Luis Suarez',
      club: 'Gremio',
      number: 9,
      position: 'Forward',
      image: 'https://i2-prod.mirror.co.uk/incoming/article28982947.ece/ALTERNATES/s1200d/0_GettyImages-1246313256.jpg'
    }
  ]
  isDisabled: boolean = true;

}
