import { Component, EventEmitter, Input, Output } from '@angular/core';
import { FavoritesService } from 'src/app/favorites/favorites.service';
import { AuthService } from 'src/app/auth/auth.service';
import { PlayersService } from '../players.service';
import { PlayerAPi } from 'src/app/types/playerAPI';

@Component({
  selector: 'app-player',
  templateUrl: './player.component.html',
  styleUrls: ['./player.component.css']
})


export class PlayerComponent {
  // @Input() player: Player =  {} as Player; 
  isFavorite: boolean = false;

  // addToFav() {
  //   this.isFavorite = true;
  //   this.favoritesService.add(this.player);
  // }

  // remove() {
  //   this.isFavorite = false;
  //   this.favoritesService.remove(this.player);
  // }

  constructor(private favoritesService: FavoritesService, private playersService: PlayersService, private authService: AuthService) {}


  isAuthenticated() {
   return this.authService.isAuthenticated;
  }

  player?: PlayerAPi;

  season: number = 2019;
  league: number = 39;


  ngOnInit(): void {
    this.playersService.getPlayers(this.season, this.league)
    .subscribe({
      next: (response: any) => {
        this.player = (response);
        console.log(this.player);
      } 
    })
  }
}
