import { Component, EventEmitter, Input, Output } from '@angular/core';
import { Player } from '../types/player';

@Component({
  selector: 'app-player',
  templateUrl: './player.component.html',
  styleUrls: ['./player.component.css']
})


export class PlayerComponent {
  @Input() player: Player =  {} as Player; 
  @Output() emitPlayer = new EventEmitter<Player>();

  addToFav() {
    this.emitPlayer.emit(this.player)
  }
}
<li class="player">
    <img [src]="player.image" [alt]="player.name" width="200" />
    <p class="title">{{ player.name | bold}}</p>
    <p class="details">{{ player.club }} # {{ player.number}}</p>
    <p class="value">Value: {{player.value | currency: 'EUR'}}</p>
    <button class="favorites" (click)="addToFav()">Add to favorite</button>
</li>
