import { Injectable } from '@angular/core';
import { Player } from '../types/player';

@Injectable({
  providedIn: 'root'
})
export class FavoritesService {
  favorites: Player[] = [];

  add(player: Player) {
    this.favorites.push(player);
  }

  remove(player: Player) {
    this.favorites.pop(player);
  }

  get() {
    return this.favorites;
  }

  constructor() { }
}
