import { Component } from '@angular/core';
import { Player } from '../types/player';
import { PlayersService } from './players.service';
import { Highlights } from '../types/highlights';
import { DomSanitizer, SafeHtml } from '@angular/platform-browser';




@Component({
  selector: 'app-players',
  templateUrl: './players.component.html',
  styleUrls: ['./players.component.css']
})


export class PlayersComponent {


  favorites: Player[] = [];
  players: Player[] = [];
  isDisabled: boolean = true;  
  highlights: Highlights[] = [];

    ngOnInit(): void {
    this.playersService.getPlayers()
    .subscribe({
      next: (response: any) => {
        this.highlights = (response);
        console.log(this.highlights);
      } 
    })
  }
  constructor(private playersService: PlayersService, private sanitizer: DomSanitizer) {
  }

 sanitizedHtml: SafeHtml = this.sanitizeHTML(this.highlights[10].embed)

  sanitizeHTML(embedHTML: string): SafeHtml {
    return this.sanitizer.bypassSecurityTrustHtml(embedHTML);
  }



}
