import { Component, EventEmitter, Input, Output } from '@angular/core';
import { FavoritesService } from 'src/app/favorites/favorites.service';
import { AuthService } from 'src/app/auth/auth.service';
import { PlayersService } from '../players.service';
import { PlayerAPi, Response } from 'src/app/types/playerAPI';

@Component({
  selector: 'app-player',
  templateUrl: './player.component.html',
  styleUrls: ['./player.component.css']
})


export class PlayerComponent {
  isFavorite: boolean = false;
  @Input() player?: Response;
  @Input() index?: number;

  addToFav() {
    this.isFavorite = true;
    if (this.player) {
       this.favoritesService.add(this.player.player.name);
    }

  }

  remove() {
    this.isFavorite = false;
    if (this.player) {
      this.favoritesService.remove(this.player.player.name);
   }
  }

  constructor(private favoritesService: FavoritesService, private playersService: PlayersService, private authService: AuthService) {}


  isAuthenticated() {
   return this.authService.isAuthenticated;
  }


}

<div class="m-2 shadow-2xl
block rounded-lg bg-white shadow-[0_2px_15px_-3px_rgba(0,0,0,0.07),0_10px_20px_-2px_rgba(0,0,0,0.04)] dark:bg-neutral-700" >
    <div
    class="relative overflow-hidden bg-cover bg-no-repeat"
    data-te-ripple-init
    data-te-ripple-color="light">

    <ng-container *ngIf="index !== undefined">
        <p>{{ (index + 1) }}</p>
    </ng-container>

    <ng-container *ngIf="player">
        <img [src]=" player.player.photo" [alt]=" player.player.name" class="object-scale-down h-48 w-60"/>
        <h5
        class="mb-2 text-xl font-medium leading-tight text-neutral-800 dark:text-neutral-50">
        {{ player.player.name }}
        </h5>
        <p class="details">
            {{ player.statistics[0].team.name }} 
            - {{ player.statistics[0].goals.total}} goals,
            {{ player.statistics[0].goals.assists}} assists
        </p>

        <div class="text-center">
        <ng-container *ngIf="isAuthenticated()">
            <button class="p-1 rounded-full hover:bg-green-600 bg-green-900 text-white" *ngIf="!isFavorite" (click)="addToFav()">Add to favorite</button>
            <button class="p-1 rounded-full hover:bg-green-600 bg-green-900 text-white" *ngIf="isFavorite" (click)="remove()">Remove from favorites</button>
        </ng-container>
       </div> 
    </ng-container>
    <ng-container *ngIf="!player" class="">
        <div class="justify-between">
            <h1>Sorry, you have surpassed the API call limit of 100 a day. Please, try again later</h1>
        </div>
    </ng-container>
    </div> 
</div>
