import { Component } from '@angular/core';
import { Player } from '../types/player';
import { PlayersService } from './players.service';
import {  PlayerAPi } from '../types/playerAPI';



@Component({
  selector: 'app-players',
  templateUrl: './players.component.html',
  styleUrls: ['./players.component.css']
})


export class PlayersComponent {

  // sanitizedHtml: SafeHtml;
  favorites: Player[] = [];
  players: Player[] = [];
  isDisabled: boolean = true;  

  player?: PlayerAPi;

  season: number = 2022;
  selectedOption: number = 140;

  options: any[] = [
    { label: 'English Premier League', value: 39 },
    { label: 'La Liga ', value: 140 },
    { label: 'Serie A', value: 135 },
    { label: 'Bundesliga', value: 78 },
    { label: 'Ligue 1', value: 61 },
    { label: 'UEFA Champions League', value: 2 },
    { label: 'UEFA Europa League', value: 3 },

  ];


// NOTE: may have to put the method inside oninit.

  onOptionChange() {
    // Handle the option change event
    console.log(this.selectedOption);
    // this.getPlayers();

  }

  // getPlayers() { 
  //   this.playersService.getPlayers(this.season, this.selectedOption)
  //   .subscribe({
  //     next: (response: any) => {
  //       this.player = (response);
  //       console.log(this.player);
  //     } 
  //   })
  // }

  // ngOnInit(): void {
  //   this.getPlayers();
  // }


  constructor(private playersService: PlayersService) {  }




}
